import arrays from './mockArrays';
import concept from './mockConcept';
import city from './mockCity';
import request from 'superagent';

const url = 'http://rielt.local';

export const fetchArrays = async () => {
	const {body} = await request.get(
    url+'/api/v1/all-arrays'
	);
	return body.data;

	// return new Promise(resolve => {
	// 	resolve(arrays);
	// });
}

