import request from 'superagent';

//const url = ''; 
const url = 'http://rielt.grechka.digital';

export const fetchArrays = async () => {
	const {body} = await request.get(url+'/api/v1/all-arrays');
	return body.data;
}

export const fetchConcept = async () => {
	const {body} = await request.get(url+'/api/v1/all-concepts');
	return body.data;
}

export const fetchCity = async () => {
	const {body} = await request.get(url+'/api/v1/all-cities');
	return body.data;
}

export const sendModal = async props => {
	await request
		.post(url+'/api/v1/set-form')
		.send({"data": JSON.stringify(props)})
		.then(res => {
		  console.log('ответ ' + JSON.stringify(res.body));
		});
}