import React from 'react';
import { Link } from 'react-router';

import classNames from 'classnames';

import {renderPrice} from 'functions';

import {Pin} from 'components/UiIcon';

export const CardArray = ({
    id,
    count,
    urlImg,
    name,
    price100,
    addClass,
    onChangeArray,
    onChangeMap
  }) => {
  const cardClass = classNames('card-array', addClass);
  return (
    <div className={cardClass}>
      <div className="card-array__left">
        <span className="card-array__count">{count}</span>
        { urlImg && <img src={urlImg} className="card-array__img" alt="" /> }
        <Link
          to={`/array/${id}`}
          className="btn btn_color_white card-array__btn-buy"
        >Смотреть массив</Link>
      </div>
      <div className="card-array__right">
        <div className="card-array__title">
          <Link
            to={`/array/${id}`}
            className="h4"
          >{name}</Link>
        </div>
        
        <div className="card-array__price">
          <span>Цена за сотку</span>
          <span>{renderPrice(price100)}</span>
        </div>
        <div className="card-array__btns">
          <button
            onClick={() => onChangeArray(id)}
            className="btn btn_color_ac card-array__btn-more"
          >Смотреть участки</button>
          <button
            onClick={() => onChangeMap(id)}
            className="btn-map card-array__btn-more"
          >
            <span>на карте</span>
            <Pin />
          </button>
        </div>
      </div>
    </div>
  );
}