import React from 'react';

import {Area, Price} from 'components/UiIcon';
import {renderArea, renderPrice} from 'functions';

export const CardSite = ({
    id,
    name,
    adress,
    area,
    price,
    openPopup
  }) => {
  return (
    <div className="card-site map-cards__item">
      <div className="card-site__left">
        <div className="card-site__title">
          <a href="#" className="h4">{name}</a>
        </div>
        <div className="card-site__adress">
          <p>{adress}</p>
        </div>
      </div>
      <div className="card-site__middle">
        <div className="card-site__area b-site-prop">
          <Area className="b-site-prop__icon" />
          <span className="b-site-prop__desc">{renderArea(area)}</span>
        </div>
        <div className="card-site__price b-site-prop">
          <Price className="b-site-prop__icon" />
          <span className="b-site-prop__desc">{renderPrice(price)}</span>
        </div>
      </div>
      <div className="card-site__right">
        <button
          className="btn btn_color_ac card-site__button"
          onClick={() => openPopup(id)}
        >Забронировать</button>
      </div>
    </div>
  );
}
