import React from 'react';

import { Link } from 'react-router';

import {renderArea, renderPrice} from 'functions';

export const CardSite2 = ({
  arrayId,
  arrayName,
  name,
  adress,
  area,
  price
}) => {
  return (
    <div className="card-site-2 psit-site__item">
      <div className="card-site-2__left">
        <div className="card-site-2__title">
          <a href="#" className="h4">{name}</a>
        </div>
        <div className="card-site-2__adress">
          <p>{adress}</p>
        </div>
      </div>
      <div className="card-site-2__middle">
        <div className="card-site-2__middle-top">
          <Link to={`/array/${arrayId}`} className="card-site-2__array">{arrayName}</Link>
        </div>
        <div className="card-site-2__middle-bottom">
          <div className="card-site-2__area b-site-prop">
            <img src="/img/ui-icon/area.svg" className="img-svg b-site-prop__icon" />
            <span className="b-site-prop__desc">{renderArea(area)}</span>
          </div>
          <div className="card-site-2__price b-site-prop">
            <img src="/img/ui-icon/price.svg" className="img-svg b-site-prop__icon" />
            <span className="b-site-prop__desc">{renderPrice(price)}</span>
          </div>
        </div>
      </div>
      <div className="card-site-2__right">
        <button className="btn btn_color_ac card-site-2__button">Купить участок</button>
      </div>
    </div>
  );
}