import React from 'react';

const DefaultItemRenderer = ({checked, option, onClick}) => {
	return (
		<span>
			<span>
				{option.label}
			</span>
			<input
				type="checkbox"
				onChange={onClick}
				checked={checked}
				tabIndex="-1"
				style={{float: 'right'}}
			/>
		</span>
	);
}

export default DefaultItemRenderer;