import React, { Component } from 'react';

class Footer extends Component {
  render() {
    return (
      <footer className="footer page-contacts__footer">
        <div className="footer__left">
          <a href="#" className="footer__logo ft-logo">
            <img src="/img/logo.svg" className="img-svg" />
          </a>
        </div>
        <div className="footer__middle">
          <div className="footer__politconf ft-politconf">
            <span>2018, ООО «Крымленд», все права защищены.</span>
            <a href="#">Политика конфиденциальости</a>
          </div>
        </div>
        <div className="footer__right">
          <div className="footer__grechka">
            <div className="ft-grechka">
              <span>Разработчик:</span>
              <a href="http://grechka.digital" target="_blank">Гречка.Digital</a>
            </div>
          </div>
        </div>
      </footer>
    );
  }
}

export default Footer;