import React, { Component } from 'react';
import { Link } from 'react-router';
import { connect } from 'react-redux';

import {fetchCity} from 'actions';
import {getCityInMenu} from 'selectors';

import classNames from 'classnames';
import InputMask from 'react-input-mask';

import {Logo, Callback, Menu} from 'components/UiIcon';

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      displayMenu: false,
      visibleMenu: false
    };

    this.toggleMenu = this.toggleMenu.bind(this);
  }

  toggleMenu() {
    const { displayMenu, visibleMenu } = this.state;

    if(!displayMenu) {
      this.setState({displayMenu: !displayMenu});
      setTimeout(() => {
        this.setState({visibleMenu: !visibleMenu});
      }, 5);
    } else {
      this.setState({visibleMenu: !visibleMenu});
      setTimeout(() => {
        this.setState({displayMenu: !displayMenu});
      }, 300);
    }
  }

  render() {
    const { home, city } = this.props;
    const { displayMenu, visibleMenu } = this.state;
    const headerClass = classNames('header', {header_home: home});
    const headerLogoClass = classNames('header__logo', 'hdr-logo', {'hdr-logo_home': home});
    const headerMenuClass = classNames('header__menu', 'hdr-menu', {'hdr-menu_home': home});
    const headerMenuContainerClass = classNames('hdr-menu__container', {'display': displayMenu}, {'visible': visibleMenu});
    const headerCallbackClass = classNames('header__callback', 'hdr-callback', {'hdr-callback_home': home});

    return (
      <header className={headerClass}>
        <div className="header__left">
          <Link
            to="/"
            className={headerLogoClass}
          >
            <Logo />
          </Link>
        </div>
        <div className="header__middle">
          <div className={headerMenuClass}>
            <button
              onClick={this.toggleMenu}
              className="hdr-menu__button"
            >
              <Menu />
            </button>
            <div className={headerMenuContainerClass}>
              <ul className="hdr-menu__left">
                <li>
                  <Link to="/all-city">Все города</Link>
                </li>
                {
                  city.length !== 0 && city.map((el, id) => {
                    return (
                      <li key={id}>
                        <Link to={`/${el.translit_name}`}>{el.name}</Link>
                      </li>
                    );
                  })
                }
              </ul>
              <ul className="hdr-menu__right">
                <li>
                  <Link to="/plots/1">Участки</Link>
                </li>
                <li>
                  <Link to="/contacts">Контакты</Link>
                </li>
              </ul>
              <div className="hdr-menu__phone">
                <a href="tel:797850060070">+7 (978) 500-600-70</a>
              </div>
            </div>
          </div>
        </div>
        <div className="header__right">
          <div className="header__phone">
            <a href="tel:797850060070" className="hdr-phone">
              <span>+7 (978) 500-600-70</span>
            </a>
          </div>
          <div className={headerCallbackClass}>
            <button className="hdr-callback__button">
              <Callback />
            </button>
            <div className="hdr-callback__dropdown hdr-cb-dd">
              <h4 className="hdr-cb-dd__title">Обратный звонок</h4>
              <div className="form-field hdr-cb-dd__input">
                <InputMask mask="+7 (999) 999 99 99" maskChar="_" className="form-field__input" placeholder="Телефон"/>
              </div>
              <button className="btn btn_color_ac hdr-cb-dd__button">Перезвоните мне</button>
            </div>
          </div>
        </div>
      </header>
    );
  }
}

const mapStateToProps = (state) => ({
  city: getCityInMenu(state)
});

const mapDispatchToProps = {
  fetchCity
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);