import React, {Component} from 'react';
import {FilterField} from 'components/MainFilter/filterField';
import MyMultiSelect from 'components/MyMultiSelect';

import DefaultItemRenderer from 'components/DefaultItemRenderer';
import DefaultValueRenderer from 'components/DefaultValueRenderer';

const statesList = [
	{
		value: 'simpheropol',
		label: 'Симферополь'
	},
	{
		value: 'alushta',
		label: 'Алушта'
	},
	{
		value: 'yalta',
		label: 'Ялта'
	},
];

class MainFilter extends Component {
	constructor() {
		super();
		this.state = {
			selected: [],
		};
	}

	handleSelectedChanged(selected) {
		this.setState({selected});
  }
  
  render() {
		const {selected} = this.state;
    return (
      <div className="page-map__filter main-filter">
        <div className="main-filter__container">
          <div className="main-filter__price">
            <FilterField
              type="price"
              name="Цена, млн. руб"
              min="2.8"
              max="9.8"
            />
          </div>
          <div className="main-filter__area">
            <FilterField
              type="area"
              name="Площадь, сотки"
            />
          </div>
          <div className="main-filter__sity">
            <div className="card-filter-field">
              <p className="card-filter-field__title">Расположение</p>
              <div className="card-filter-field__inputs">
                <div className="card-filter-field__select multi-select">

                  <div className="dropdown">
                    <div className="dropdown-heading">
                      <span className="dropdown-heading-value">
                        <span>не выбрано</span>
                      </span>
                      <span className="dropdown-heading-loading-container"></span>
                      <span className="dropdown-heading-dropdown-arrow">
                        <span></span>
                      </span>
                    </div>

                    {/* <div className="dropdown-content">
                      <div className="select-panel">
                        <div>
                          <input type="text" placeholder="Search">
                        </div>
                        <label className="select-item">
                          <span className="item-renderer">
                            <input type="checkbox" tabindex="-1" value="on">
                            <span>Select All</span>
                          </span>
                        </label>
                        <ul className="select-list">
                          <li>
                            <label className="select-item">
                              <span className="item-renderer">
                                <input type="checkbox">
                                <span>Brian Genisio</span>
                              </span>
                            </label>
                          </li>
                          <li>
                            <label className="select-item">
                              <span className="item-renderer">
                                <input type="checkbox">
                                <span>Brian Genisio 2</span>
                              </span>
                            </label>
                          </li>
                          <li>
                            <label className="select-item">
                              <span className="item-renderer">
                                <input type="checkbox">
                                <span>Brian Genisio 2</span>
                              </span>
                            </label>
                          </li>
                        </ul>
                      </div>
                    </div> */}

                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="main-filter__concept">
            <div className="card-filter-field">
              <p className="card-filter-field__title">Концепция</p>
              <div className="card-filter-field__inputs">
                <MyMultiSelect
                  addClass="card-filter-field__select"
                  options={statesList}
                  onSelectedChanged={this.handleSelectedChanged.bind(this)}
                  selected={selected}
                  ItemRenderer={DefaultItemRenderer}
                  valueRenderer={DefaultValueRenderer}
                  disableSearch={true}
                  selectAllLabel="Все города"
                />
              </div>
            </div>
          </div>
          <div className="main-filter__button">
            <div className="card-filter-field">
              <p className="card-filter-field__title"></p>
              <div className="card-filter-field__inputs">
                <button className="btn btn_color_ac card-filter-field__button">Показать</button>
              </div>
            </div>
          </div>
        </div>
        <div className="main-filter__bg"></div>
      </div>
    );
  }
}

export default MainFilter;