import React, {Fragment} from 'react';

const DefaultItemRender = ({checked, option, onClick}) => {
	return (
		<Fragment>
			<input
				type="checkbox"
				onChange={onClick}
				checked={checked}
				tabIndex="-1"
				style={{float: 'right'}}
			/>
			<span>
				{option.label}
			</span>
		</Fragment>
	);
}

export default DefaultItemRender;