const DefaultValueConceptRender = (selected, options) => {
	let lengthSelected = 0;
	selected.forEach(el => {
		if(typeof el !== 'undefined') lengthSelected++
	});

	let lengthOptions = 0;
	options.forEach(el => {
		if(typeof el !== 'undefined') lengthOptions++
	});

	if (lengthSelected === 0) {
		return 'Выберите концепцию...';
	}

	if (lengthSelected === lengthOptions) {
		return 'Выбраны все концепции';
	}

	if(aaa(lengthSelected) === 1) {
		return `Выбрана ${lengthSelected} концепция`;
	}

	if(aaa(lengthSelected) === 2) {
		return `Выбрано ${lengthSelected} концепции`;
	}

	return `Выбрано ${lengthSelected} концепций`;
}

export default DefaultValueConceptRender;

const aaa = number => {
	const num = +number;
	const num_1 = +num.toString().substr(-2, 2);
	if(num_1 > 10 && num_1 < 20) {
		return 0;
	} else {
		let num_2 = +num.toString().slice(-1);
		if( num_2 === 1 ) return 1;
		if( num_2 > 1 && num_2 < 5 ) return 2;
		return 0
	}
}