import React from 'react';

const ArrayContent = ({desc}) => {
  return (
    <section className="page-array__content parr-content">
      <div className="container">
        <div className="row">
          <div className="col-md-7">
            <div
              className="b-content parr-content__content"
              dangerouslySetInnerHTML={desc}
            />
          </div>
          
          <div className="col-md-5">
            <div className="parr-content__img">
              <img src="/content/array-in-map.jpg" />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ArrayContent;