import React from 'react';
import classNames from 'classnames';

const ArrayContent = ({desc, mapImg}) => {
  const contentClassName = classNames({
    'col-md-7': mapImg != '',
    'col-md-12': mapImg == ''
  });

  const mapClassName = classNames({
    'col-md-5': desc != '',
    'col-md-12': desc == ''
  });

  return (
    <section className="page-array__content parr-content">
      <div className="container">
        <div className="row">
          <div className={contentClassName}>
            <div
              className="b-content parr-content__content"
              dangerouslySetInnerHTML={desc}
            />
          </div>
          
          { mapImg != '' && <div className={mapClassName}>
            <div className="parr-content__img">
              <img src={mapImg} />
            </div>
          </div> }
        </div>
      </div>
    </section>
  );
}

export default ArrayContent;