import React from 'react';

import classNames from 'classnames';

const ArrayIcons = ({infrastructure}) => {
  return (
    <section className="page-array__icons parr-icons">
      <div className="container">
        <div className="row">
          {
            typeof infrastructure === 'object' && infrastructure.map((element, id) => {
              const colClass = classNames(
                'col-sm-6 col-lg-5',
                {
                  'offset-lg-1': id % 2 === 1
                }
              );
              return (
                <div
                  key={id}
                  className={colClass}
                >
                  <div className="parr-icons__item card-text-info">
                    <img
                      src={`/api${element.img}`}
                      className="img-svg card-text-info__icon"
                    />
                    <h4 className="card-text-info__title">{element.title}</h4>
                    <div className="card-text-info__desc b-content">
                      <p>{element.description}</p>
                    </div>
                  </div>
                </div>
              );
            })
          }
        </div>
      </div>
    </section>
  );
}

export default ArrayIcons;
