import React from 'react';

const ArrayMain = ({id, name, mainImg}) => {
  return (
    <section className="page-array__main parr-main">
      <img src={mainImg} className="parr-main__bg" />
      
      <div className="container parr-main__container">
        <div className="row">
          <div className="col-lg-8">
            <h1 className="parr-main__title">{name}</h1>
          </div>
          <div className="col-lg-4">
            <button className="btn btn_color_ac parr-main__btn-buy">Купить массив</button>
            <button className="btn btn_color_white parr-main__btn-view-site">Смотреть участки</button>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ArrayMain;