import React from 'react';

const ArrayMedia = ({}) => {
  return (
    <section className="page-array__media parr-media">
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <div className="parr-media__panorama">
              <a href="#" target="_blank" className="btn btn_color_white parr-media__btn-panorama">Панорама 360</a>
            </div>
          </div>
          <div className="col-md-6">
            <div className="parr-media__video">
              <iframe src="https://www.youtube.com/embed/StZcUAPRRac" frameBorder="0" allow="autoplay; encrypted-media" allowFullScreen></iframe>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ArrayMedia;