import React from 'react';

const ArrayMedia = ({panoramaUrl, panoramaImg, videoId}) => {
  return (
    <section className="page-array__media parr-media">
      <div className="container">
        <div className="row">
          { panoramaUrl !== '' && <div className="col-md-6">
            <div
              className="parr-media__panorama"
              style={{
                backgroundImage: `url('${panoramaImg}')`,
                backgroundPosition: '50% 50%',
                backgroundSize: 'cover'
              }}
            >
              <a
                href={panoramaUrl}
                target="_blank"
                className="btn btn_color_white parr-media__btn-panorama"
              >Панорама 360</a>
            </div>
          </div> }
          { videoId !== '' && <div className="col-md-6">
            <div className="parr-media__video">
              <iframe
                src={`https://www.youtube.com/embed/${videoId}`}
                frameBorder="0"
                allow="autoplay; encrypted-media"
                allowFullScreen
              />
            </div>
          </div> }
        </div>
      </div>
    </section>
  );
}

export default ArrayMedia;