import React from 'react';

import {renderArea, renderPrice, renderCoords} from 'functions';

const ArrayProps = ({area, price, count, coords, legalSystem}) => {
  return (
    <section className="page-array__props parr-props">
      <div className="container">
        <div className="row">
          <div className="col-lg-4">
            <div className="parr-props__main">
              <div className="parr-props__area">
                <span>Площадь</span>
                <span>{renderArea(area)}</span>
              </div>
              <div className="parr-props__price">
                <span>Цена</span>
                <span>{renderPrice(price)}</span>
              </div>
            </div>
          </div>
          <div className="col-sm-5 col-lg-3 offset-lg-1">
            <div className="parr-props__dop">
              <div className="parr-props__row">
                <span>Участков</span>
                <span>{count}</span>
              </div>
              <div className="parr-props__row">
                <span>Координаты</span>
                <span>{renderCoords(coords)}</span>
              </div>
            </div>
          </div>
          <div className="col-sm-5 offset-sm-1 col-lg-3 offset-lg-1">
            <div className="parr-props__dop">
              <div className="parr-props__row">
                <span>Собственник участка</span>
                <span>{legalSystem}</span>
              </div>
              <div className="parr-props__row">
                <span>Подтвержденное право</span>
                <span>госакты</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ArrayProps;