import React from 'react';

import {ArrowLeft, ArrowRight} from 'components/UiIcon';

const ArraySlider = ({}) => {
  return (
    <div className="page-array__slider parr-slider">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="parr-slider__slider b-slider">
              <button className="b-slider__prev">
                <ArrowLeft />
              </button>
              <button className="b-slider__next">
                <ArrowRight />
              </button>
              <div className="b-slider__dots">
                <button className="b-slider__dot"></button>
                <button className="b-slider__dot"></button>
                <button className="b-slider__dot active"></button>
                <button className="b-slider__dot"></button>
                <button className="b-slider__dot"></button>
              </div>
              <div className="b-slider__container">
                <div className="b-slider__item">
                  <img src="/content/array-slider.jpg" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ArraySlider;