import React, {Component, Fragment} from 'react';
import Header from 'components/Header';
import MainFilter from 'components/MainFilter';

class Home extends Component {
  render() {
    return (
      <Fragment>
        <Header
          home={true}
        />
        <main className="main page-home">
          <h1 className="page-home__title">Продажа участков в Крыму</h1>
          <div className="page-home__desc">
            <p>Поспешите забронировать наиболее выгодные предложения, <br />пока это не сделал кто-то другой!</p>
          </div>
          <MainFilter
            pageHome={true}
          />
        </main>
      </Fragment>
    );
  }
}

export default Home;