import React, {Component} from 'react';
import Header from 'components/Header';

class Home extends Component {
  render() {
    return (
      <div>
        <Header
          home={true}
        />
        <main className="main page-home">
          <h1 className="page-home__title">Продажа участков в Крыму</h1>
          <div className="page-home__desc">
            <p>Поспешите забронировать наиболее выгодные предложения, <br />пока это не сделал кто-то другой!</p>
          </div>
          <div className="page-home__filter home-filter">
            <div className="home-filter__price">
              <div className="card-filter-field">
                <p className="card-filter-field__title">Цена, млн. руб</p>
                <div className="card-filter-field__inputs">
                  <div className="form-field form-field_left form-field_label card-filter-field__first-input">
                    <label htmlFor="priceMin" className="form-field__label">от</label>
                    <input id="priceMin" type="number" className="form-field__input" placeholder="2.8" />
                  </div>
                  <div className="form-field form-field_right form-field_label card-filter-field__last-input">
                    <label htmlFor="priceMax" className="form-field__label">от</label>
                    <input id="priceMax" type="number" className="form-field__input" placeholder="9.8" />
                  </div>
                </div>
              </div>
            </div>
            <div className="home-filter__area">
              <div className="card-filter-field">
                <p className="card-filter-field__title">Площадь, сотки</p>
                <div className="card-filter-field__inputs">
                  <div className="form-field form-field_left form-field_label card-filter-field__first-input">
                    <label htmlFor="areaMin" className="form-field__label">от</label>
                    <input id="areaMin" type="number" className="form-field__input" />
                  </div>
                  <div className="form-field form-field_right form-field_label card-filter-field__last-input">
                    <label htmlFor="areaMax" className="form-field__label">от</label>
                    <input id="areaMax" type="number" className="form-field__input" />
                  </div>
                </div>
              </div>
            </div>
            <div className="home-filter__sity">
              <div className="card-filter-field">
                <p className="card-filter-field__title">Расположение</p>
                <div className="card-filter-field__inputs">
                  <div className="card-filter-field__select multi-select">
                    <div className="dropdown">
                      <div className="dropdown-heading">
                        <span className="dropdown-heading-value">
                          <span>не выбрано</span>
                        </span>
                        <span className="dropdown-heading-loading-container"></span>
                        <span className="dropdown-heading-dropdown-arrow">
                          <span></span>
                        </span>
                      </div>
                      {/* <div className="dropdown-content">
                        <div className="select-panel">
                          <div>
                            <input type="text" placeholder="Search">
                          </div>
                          <label className="select-item">
                            <span className="item-renderer">
                              <input type="checkbox" tabindex="-1" value="on">
                              <span>Select All</span>
                            </span>
                          </label>
                          <ul className="select-list">
                            <li>
                              <label className="select-item">
                                <span className="item-renderer">
                                  <input type="checkbox">
                                  <span>Brian Genisio</span>
                                </span>
                              </label>
                            </li>
                            <li>
                              <label className="select-item">
                                <span className="item-renderer">
                                  <input type="checkbox">
                                  <span>Brian Genisio 2</span>
                                </span>
                              </label>
                            </li>
                            <li>
                              <label className="select-item">
                                <span className="item-renderer">
                                  <input type="checkbox">
                                  <span>Brian Genisio 2</span>
                                </span>
                              </label>
                            </li>
                          </ul>
                        </div>
                      </div> */}
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="home-filter__concept">
              <div className="card-filter-field">
                <p className="card-filter-field__title">Концепция</p>
                <div className="card-filter-field__inputs">
                  <div className="card-filter-field__select multi-select">

                    <div className="dropdown">
                      <div className="dropdown-heading">
                        <span className="dropdown-heading-value">
                          <span>не выбрано</span>
                        </span>
                        <span className="dropdown-heading-loading-container"></span>
                        <span className="dropdown-heading-dropdown-arrow">
                          <span></span>
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="home-filter__button">
              <div className="card-filter-field">
                <p className="card-filter-field__title"></p>
                <div className="card-filter-field__inputs">
                  <button className="btn btn_color_ac card-filter-field__button">Показать</button>
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    );
  }
}

export default Home;