import React, {Component} from 'react';
import MultiSelect from '@khanacademy/react-multi-select';

import DefaultItemRenderer from 'components/DefaultItemRenderer';
import DefaultValueRenderer from 'components/DefaultValueRenderer';

const statesList = [
	{
		value: 'simpheropol',
		label: 'Симферополь'
	},
	{
		value: 'alushta',
		label: 'Алушта'
	},
	{
		value: 'yalta',
		label: 'Ялта'
	},
];

class Select extends Component {
	constructor() {
		super();
		this.state = {
			selected: [],
		};
	}

	handleSelectedChanged(selected) {
		this.setState({selected});
	}

	render() {
		const {selected} = this.state;
		return (
			<div>
				<MultiSelect
					options={statesList}
					onSelectedChanged={this.handleSelectedChanged.bind(this)}
					selected={selected}
					ItemRenderer={DefaultItemRenderer}
					valueRenderer={DefaultValueRenderer}
					disableSearch={true}
					selectAllLabel="Все города"
				/>
			</div>
		);
	}
}

export default Select;