export const renderPrice = price => {
  if( parseFloat(price) >= 1 ) {
    return `${price} млн. руб.`
  } else {
    return `${parseInt(price*1000)} тыс. руб.`
  }
}

export const renderArea = area => {
  if( parseFloat(area) >= 1 ) {
    return `${area} га`;
  } else {
    return `${parseInt(area * 100)} соток`;
  }
}

export const renderCoords = coords => {
  return `${coords.split(',')[0]}, ${coords.split(',')[1]}`;
}