export const renderPrice = oldPrice => {
  const price = oldPrice / 1000000;
  if( parseInt(price) >= 1 ) {
    if( price.toString().includes('.') ) {
      return `${parseFloat(price).toFixed(1)} млн. руб.`;
    } else {
      return `${parseInt(price)} млн. руб.`;
    }
  } else {
    return `${parseInt(price*1000)} тыс. руб.`;
  }
}

export const renderArea = oldArea => {
  const area = +oldArea / 100;
  if( parseFloat(area) >= 1 ) {
    if( area.toString().includes('.') ) {
      if( parseInt(area % 1 * 100) % 10 === 0 ) {
        return `${parseFloat(area).toFixed(1)} га`;
      } else {
        return `${parseInt(area * 100) / 100} га`;
      }
    } else {
      return `${parseFloat(area)} га`;
    }
  } else {
    return `${oldArea} соток`;
  }
}

export const renderCoords = coords => {
  return `${coords.split(',')[0]}, ${coords.split(',')[1]}`;
}

export const renderSearchResult = (arrays, plots) => {
  const arrayEnd = ['массивах', 'массиве', 'массивах'];
  const plotsEnd = ['участков', 'участок', 'участка'];

  function aaa(number) {
    const num = +number;
    const num_1 = +num.toString().substr(-2, 2);
    if(num_1 > 10 && num_1 < 20) {
      return 0;
    } else {
      let num_2 = +num.toString().slice(-1);
      if( num_2 === 1 ) return 1;
      if( num_2 > 1 && num_2 < 5 ) return 2;
      return 0
    }
  }

  if( arrays === 0 && plots === 0 ) {
    return 'К сожалению, по Вашему запросу ничего не найдено';
  } else {
    return `Найдено ${plots} ${plotsEnd[aaa(plots)]} в ${arrays} ${arrayEnd[aaa(arrays)]}`
  }
  
}
