import R from 'ramda';

import {
	UPDATE_FILTER_PROPS,
	CLEAR_FILTER_PROPS
} from 'actionTypes';

const initialState = {
	price_min: null,
	price_max: null,
	area_min: null,
	area_max: null,
	city_id: [],
	concept_id: []
};

export default (state = initialState, {type, payload}) => {
	if( type == UPDATE_FILTER_PROPS ) {
		console.log('update', payload);
		return R.merge(state, payload);
	} else if( type == CLEAR_FILTER_PROPS ) {
		console.log('clear', state);
		return R.merge(state, {});
	} else {
		return state;
	}
}