import R from 'ramda';

import {
	UPDATE_FILTER_PROPS,
	CLEAR_FILTER_PROPS
} from 'actionTypes';

const initialState = {
	price_min: '',
	price_max: '',
	area_min: '',
	area_max: '',
	city_id: [],
	concept_id: []
};

export default (state = initialState, {type, payload}) => {
	if( type === UPDATE_FILTER_PROPS ) {
		return R.merge(state, payload);
	} else if( type === CLEAR_FILTER_PROPS ) {
		return initialState;
	} else {
		return state;
	}
}