import R from 'ramda';

import {
	FETCH_ARRAYS_SUCCESS
} from 'actionTypes';

const initialState = {};

export default (state = initialState, {type, payload}) => {
	switch(type) {
    case FETCH_ARRAYS_SUCCESS:
      let plots = [];
      payload.forEach(array => {
        array.land_plots.forEach(element => {
          element.array_name = array.name;
          plots.push(element);
        });
      });
			return R.merge(state, plots);
		default:
			return state;
	}
}