import R from 'ramda';

const getPhoneById = (state, id) => {
	return R.prop(id, state.phones);
}

export const getPhones = state => {
	const phones = R.map(id => getPhoneById(state, id), state.phonesPage.ids);
	return phones;
}

const getArrayById = (state, id) => {
	return R.prop(id, state.arrays);
}

export const getArrays = state => {
	let phones = [];
	for( var key in state.arrays ) {
		phones[key] = state.arrays[key];
	}
	return phones;
}